import React from 'react';
import styled from 'styled-components';

/* Components -------- */
import FormGroup from '../../../common/Forms/FormGroup.jsx';

const formFields = {
    nameField: {
        id: 'userName',
        label: 'Name',
        inputType: 'text',
        value: '',
    },
    emailField: {
        id: 'userEmail',
        label: 'Email',
        inputType: 'email',
        value: '',
    },
    messageField: {
        id: 'userMessage',
        label: 'Message',
        inputType: 'textarea',
        value: '',
    },
}



const ContactForm = () => {

    const handleChange = (event, formField) => {
        console.log('Detecting a Change');
//grandpa
    }

    return (
        <ContactFormStyled className='ContactForm'>
            <form>
                <FormGroup 
                    formField = { formFields.nameField } 
                    onChange={handleChange }
                />
                <FormGroup 
                    formField = { formFields.emailField} 
                    onChange={ handleChange }
                />
                <FormGroup 
                    formField = { formFields.messageField }
                    onChange={ handleChange }
                />
            </form>
        </ContactFormStyled>
    );
}

export default ContactForm;

const ContactFormStyled = styled.div`

`;
